import React from 'react';
import {Layout, Card, Col, Row, Descriptions, Divider, Image} from 'antd';
import store from './store/store.json';
import {FlagOutlined, LinkOutlined} from "@ant-design/icons";
import Header from "./component/Header";

const { Footer, Content } = Layout;

const About = ({ about }) => (
  <div>
    <h1>{about.name}</h1>
    <p>{about.data}</p>
  </div>
);

const Manufactures = ({ manufacturers }) => {
  const { name, description, data } = manufacturers;
  return (
    <div className="section manufacturers">
      <h2 className="title" style={{textAlign: "center"}}>{name}</h2>
      <p className="description" style={{textAlign: "center"}}>{description}</p>
      <Row>
        {data.map((item) => (
          <Col xs={12} sm={8} md={8} lg={8} xl={8} key={item.key}>
            <Card className="card"
              title={item.name}
              size="small"
              extra={
                <a href={item.site} target={"_blank"} rel={"noreferrer noopener"}><LinkOutlined /></a>
              }>
              <Image style={{width: "100%", textAlign: "center"}}
                src="error"
                fallback="data:image/png;base64,"
              />
              <p className="card-description">{item.description}</p>
              <p className="card-country"><FlagOutlined className="card-icon" /> {item.country}</p>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

const Services = ({ services }) => {
  const { name, data } = services;
  return (
    <div>
      <h2>{name}</h2>
      {data.map((service, index) => (
        <Card key={index} title={service.name} size="small">
          <p>{service.description}</p>
        </Card>
      ))}
    </div>
  );
};

const Contacts = ({ contacts }) => {
  const { name, data } = contacts;
  return (
    <div>
      <Descriptions title={name}>
        {data.map((item) => (
          <Descriptions.Item key={item.key} label={item.label}>
            {Array.isArray(item.children) ? item.children.join(', ') : item.children}
          </Descriptions.Item>
        ))}
      </Descriptions>
    </div>
  );
};

function App() {
  const { about, manufacturers, services, contacts } = store;
  return (
  <Layout>
    <Header className="header" workSchedule={store.workSchedule}/>
    <Content>
      { about && <About className="about" about={about} ></About> }
      <Divider />
      { manufacturers && <Manufactures className="manufacturers" manufacturers={manufacturers} ></Manufactures> }
      <Divider />
      { services && <Services className="services" services={services} ></Services> }
      <Divider />
      { contacts && <Contacts className="contacts" contacts={contacts} ></Contacts> }
    </Content>
    <Footer className="footer">
      @copyright 2025 by Avtosklo Zelena
    </Footer>
  </Layout>
  );
}

export default App;